sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

         var rightnow = new GlideDateTime();
	var overdueneedittask = new GlideRecord ('x_58872_needit_needit_task');
	overdueneedittask.addQuery('due_date','<=', rightnow);
	overdueneedittask.addQuery('state','<',3);
	overdueneedittask.addQuery('assigned_to',current.assigned_to);
	overdueneedittask.query();
	template.print("In addition to this NeedIt Task, you have " +(overdueneedittask.getRowCount()-1) + " more overdue NeedIt task.");
})(current, template, email, email_action, event);
